import React from "react";

/**
 * SettingsPanel component - displays when a node is selected
 * Shows node-specific properties and allows editing
 * Currently supports Text Node editing, but designed to be extensible
 * Now includes a delete button to remove nodes
 */
const SettingsPanel = ({
  selectedNode,
  onNodeUpdate,
  onClose,
  onDeleteNode,
}) => {
  // If no node is selected, don't render anything
  if (!selectedNode) {
    return null;
  }

  // Handle text change for text nodes
  const handleTextChange = (event) => {
    const newText = event.target.value;
    onNodeUpdate(selectedNode.id, { text: newText });
  };

  // Handle going back to nodes panel
  const handleBack = () => {
    onClose();
  };

  // Handle delete node
  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this node?")) {
      onDeleteNode(selectedNode.id);
      onClose(); // Close settings panel after deletion
    }
  };

  return (
    <div className="settings-panel">
      {/* Header with back button, title, and delete button */}
      <div className="settings-panel-header">
        <div className="settings-panel-header-left">
          <button className="back-button" onClick={handleBack}>
            ‚Üê
          </button>
          <h3 className="settings-panel-title">Message</h3>
        </div>
        <button
          className="delete-button"
          onClick={handleDelete}
          title="Delete node">
          
        </button>
      </div>

      {/* Settings content based on node type */}
      <div className="settings-panel-content">
        {selectedNode.type === "textNode" && (
          <div className="text-settings">
            <label className="settings-label">Text</label>
            <textarea
              className="settings-textarea"
              value={selectedNode.data?.text || ""}
              onChange={handleTextChange}
              placeholder="Enter your message..."
              rows={4}
            />
          </div>
        )}

        {/* Future node types can be handled here:
        {selectedNode.type === 'conditionNode' && (
          <div className="condition-settings">
            <label className="settings-label">Condition</label>
            <input
              className="settings-input"
              value={selectedNode.data?.condition || ''}
              onChange={(e) => onNodeUpdate(selectedNode.id, { condition: e.target.value })}
              placeholder="Enter condition..."
            />
          </div>
        )}
        */}
      </div>
    </div>
  );
};

export default SettingsPanel;
