import React from 'react';
import { Handle, Position } from 'reactflow';

/**
 * TextNode component represents a message node in the chatbot flow
 * Features:
 * - Source handle (right side) - can only have one outgoing edge
 * - Target handle (left side) - can have multiple incoming edges
 * - Editable text content
 * - WhatsApp-style design
 */
const TextNode = ({ data, isConnectable }) => {
  return (
    <div className="text-node">
      {/* Node header with icon and title */}
      <div className="text-node-header">
        <div className="text-node-title">
          <span className="text-node-icon"></span>
          <span>Send Message</span>
        </div>
        <div className="whatsapp-icon"></div>
      </div>
      
      {/* Node body with editable text */}
      <div className="text-node-body">
        {data.text || 'Enter message...'}
      </div>
      
      {/* Target handle - can accept multiple incoming connections */}
      <Handle
        type="target"
        position={Position.Left}
        id="target"
        isConnectable={isConnectable}
        className="handle target-handle"
      />
      
      {/* Source handle - can only have one outgoing connection */}
      <Handle
        type="source"
        position={Position.Right}
        id="source"
        isConnectable={isConnectable}
        className="handle source-handle"
      />
    </div>
  );
};

export default TextNode;
